import React, { useEffect, useState } from "react";
import { url } from "../Constants";
import { Grid, TextField, Button, FormControlLabel, RadioGroup, Radio, Autocomplete } from "@mui/material";
import { padding } from "@mui/system";

const Dataentry = () => {
    const [name, setName] = useState('');
    const [dob, setDob] = useState('');
    const [gender, setGender] = useState('');
    const [married, setMarried] = useState(false);
    const [weddingdate, setWeddingdate] = useState('');
    const [emailid, setEmailid] = useState('');
    const [mobile, setMobile] = useState();
    const [address, setAddress] = useState('');
    const [familyhead, setFamilyhead] = useState('');
    const [baptized, setBaptized] = useState(false);
    const [android, setAndroid] = useState(true);
    const [familyid, setFamilyid] = useState('');
    const [selfid, setSelfid] = useState('');
    const [familyheads, setFamilyheads] = useState([]);
    const [validity, setvalidity] = useState();

    const [data, setData] = useState({
        "name": '',
        "dob": '', 
        "gender": '',
        "weddingdate":'',
        "emailid":'',
        "mobile":'',
        "address":'',
        "familyhead":'',
        "familyid":'',
        "selfid":'',
        "android":false,
        "baptized":false,
        "married":false

    })

    const str = dob.split('-');
    const today = new Date();
    const mon = today.getMonth();
    const year = today.getFullYear();
    let age;



    let k = year - 1;

    if (mon >= str[1]) {
        age = year - str[0];


    }
    else {
        age = k - str[0];

    }

    const addrecords = () => {
        if ((familyid && familyhead && address && selfid && name && gender && (!mobile.length < 10)) !== '') {
            setvalidity(true);
        }
        else {
            setvalidity(false);
        }

        if (validity) {

            let Familyid = familyid;
            let Selfid = selfid;
            let Name = name;
            let Dob = dob;
            let Age = age;
            let Gender = gender;
            let Married = married;
            let Weddingdate = weddingdate;
            let Emailid = emailid;
            let Mobile = mobile;
            let Address = address;
            let Familyhead = familyhead;
            let Baptized = baptized;
            let Android = android;


            fetch(`${url}/jehovanissi/backend/apis/member_details.php`, {
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({

                    name: Name,
                    dob: Dob,
                    age: Age,
                    gender: Gender,
                    married: Married,
                    weddingdate: Weddingdate,
                    emailid: Emailid,
                    mobile: Mobile,
                    address: Address,
                    familyhead: Familyhead,
                    baptized: Baptized,
                    android: Android,
                    familyid: Familyid,
                    selfid: Selfid
                })
            })
                .then(res => res.json())
                .then((result) => {
                    alert(result);
                    window.location.reload();
                }).catch((error) => {
                    console.error(error);
                });
        }
        else {
            alert("Input All required");

        }



    }

    const searchfamilyhead = () => {
        fetch(`${url}/jehovanissi/backend/apis/searchfamilyhead.php`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },

        })
            .then(res => res.json())
            .then((result) => {
                let filteredresult = [...new Set(result)];
                setFamilyheads(filteredresult);
            }).catch((error) => {
                console.error(error);
            });
    }
    useEffect(() => {
        searchfamilyhead();
    }, [])

    return (
        <Grid
            direction="column"
            alignItems={"stretch"}
            justifyItems={"stretch"}
            container
        >
            <Grid
                direction={"column"}
                alignItems={"center"}
                justifyItems={"stretch"}
                container
                item
            >
                <Grid>
                    <TextField
                        style={{ width: 300, marginRight: 15 }}
                        name="familyid"
                        value={familyid}
                        margin="dense"
                        variant="standard"
                        onChange={(event) => setFamilyid(event.target.value.toUpperCase())}
                        placeholder="Family ID"
                        label="Family ID"
                    />
                    <TextField
                        name="selfid"
                        style={{ width: 300, marginRight: 15 }}
                        value={selfid}
                        margin="dense"
                        variant="standard"
                        onFocus={() => setSelfid(familyid.slice(3))}
                        placeholder="Self ID"
                        label="Self ID"
                    />
                </Grid>
                <Grid>
                    <TextField
                        name="name"
                        value={name}
                        style={{ width: 300, marginRight: 15 }}
                        margin="dense"
                        variant="standard"
                        onChange={(event) => {
                            const str = event.target.value.split(' ');
                            for (let i = 0; i < str.length; i++) {

                                str[i] = str[i].charAt(0).toUpperCase() + str[i].substring(1);
                            }
                            const Nameoff = str.join(' ');
                            setName(Nameoff)
                        }}
                        placeholder="Enter Name"
                        label="Enter Name"
                    />
                    <TextField
                        type="date"
                        name="dob"
                        style={{ width: 300, marginRight: 15 }}
                        margin="dense"
                        value={dob}
                        variant="standard"
                        onChange={(event) => setDob(event.target.value)}
                        placeholder="Enter DOB"
                        label="Enter DOB"
                    />
                </Grid>
                <RadioGroup
                    row
                    aria-label="gender"
                    name="row-radio-buttons-group"
                    style={{
                        width: 300,
                        justifyContent: "space-evenly"
                    }}
                >
                    <FormControlLabel
                        value="male"
                        name="gender"
                        label={"Male"}
                        control={<Radio />}
                        onChange={(event) => setGender(event.target.value)} />
                    <FormControlLabel
                        value="female"
                        name="gender"
                        label={"Female"}
                        control={<Radio />}
                        onChange={(event) => setGender(event.target.value)} />
                </RadioGroup>
                <RadioGroup
                    row
                    aria-label="martial-status"
                    name="row-radio-buttons-group"
                    style={{
                        width: 300,
                        justifyContent: "space-evenly"
                    }}
                >
                    <FormControlLabel
                        value={true}
                        label={"Married"}
                        control={<Radio />}
                        onChange={(event) => { setMarried(event.target.value === 'true' ? event.target.value = true : event.target.value = false); setdisplay(event.target.value === "true" ? "block" : "none") }} />
                    <FormControlLabel
                        value={false}
                        control={<Radio />}
                        label="Unmarried"
                        onChange={(event) => { setMarried(event.target.value === 'false' ? event.target.value = false : event.target.value = true); setdisplay(event.target.value === "false" ? "none" : "block") }} />
                </RadioGroup>

                <TextField
                    type="date"
                    label="Wedding Date"
                    style={{ width: 300, marginRight: 15 }}
                    margin="dense"
                    variant="standard"
                    defaultValue={"01/01/2020"}
                    onChange={(event) => setWeddingdate(event.target.value)}
                />
                <Grid>
                    <TextField
                        name="emailid"
                        margin="dense"
                        label="Email ID"
                        style={{ width: 300, marginRight: 15 }}
                        variant="standard"
                        onChange={(event) => setEmailid(event.target.value)}
                        value={emailid}
                    />

                    <TextField
                        name="mobile"
                        style={{ width: 300, marginRight: 15 }}
                        label="Mobile"
                        margin="dense"
                        variant="standard"
                        value={mobile}
                        onChange={(event) => {
                            let patt = /[^0-9]/g;
                            const ph = event.target.value.replace(patt, '');
                            setMobile(ph.slice(0, 10));
                        }} />
                </Grid>

                <Grid>
                    <TextField
                        name="address"
                        label="Address"
                        style={{ width: 300, marginRight: 15 }}
                        margin="dense"
                        variant="standard"
                        value={address}
                        onChange={(event) => setAddress(event.target.value)}
                    />
                    <Autocomplete
                        options={familyheads}
                        renderInput={(params) =>
                            <TextField
                                {...params}
                                name="familyhead"
                                value={familyhead}
                                margin="dense"
                                variant="standard"
                                label="Family Head"
                                onChange={(event) => {
                                    const str = event.target.value.split(' ');
                                    for (let i = 0; i < str.length; i++) {
                                        str[i] = str[i].charAt(0).toUpperCase() + str[i].substring(1);
                                    }
                                    const familyheadname = str.join(' ');
                                    setFamilyhead(familyheadname)
                                }}
                            />
                        }
                    />
                </Grid>
                <RadioGroup
                    row
                    aria-label="Baptizm"
                    name="row-radio-buttons-group"
                    style={{
                        width: 300,
                        justifyContent: "space-evenly"
                    }}
                >
                    <FormControlLabel
                        control={<Radio />}
                        value={true}
                        label={"Baptized"}
                        onChange={(event) => setBaptized(event.target.value === 'true' ? event.target.value = true : event.target.value = false)}
                    />
                    <FormControlLabel
                        value={false}
                        control={<Radio />}
                        label={"Not Baptized"}
                        onChange={(event) => setBaptized(event.target.value === 'false' ? event.target.value = false : event.target.value = true)}
                    />
                </RadioGroup>

                <RadioGroup
                    row
                    aria-label="mobile-type"
                    name="row-radio-buttons-group"
                    style={{
                        width: 300,
                        justifyContent: "space-evenly"
                    }}
                >
                    <FormControlLabel
                        label={"Android"}
                        value={true}
                        control={<Radio />}
                        onChange={(event) => setAndroid(event.target.value === 'true' ? event.target.value = true : event.target.value = false)} />
                    <FormControlLabel
                        label={"Normal"}
                        value={false}
                        control={<Radio />}
                        onChange={(event) => setAndroid(event.target.value === 'false' ? event.target.value = false : event.target.value = true)} />
                </RadioGroup>

                <Button
                    variant="contained"
                    onClick={addrecords} >
                    Add to records</Button>
            </Grid>
            <Grid>
            </Grid>
        </Grid>
    )
}

export default Dataentry;
