import { Button, Grid, Select, Table, TableHead, TableRow, IconButton, TextField, TableCell, MenuItem, Autocomplete, Typography } from "@mui/material";
import React, { useEffect, useState } from "react";
import DeleteOutlineIcon from '@mui/icons-material/DeleteOutline';
import { url } from "../../Constants";
import AddCircleOutline from "@mui/icons-material/AddCircleOutline";
import { CAlert } from "@coreui/react";
import { AlertMessage } from "../Support/AlertMessage";

const Editaccounting = () => {
    const [date, setdate] = useState('');
    const [dateset, isDateset] = useState(false);
    const [data, setdata] = useState([]);
    const [data2, setdata2] = useState({});
    const [click, setclick] = useState(0);
    const [tempname, settempname] = useState('');
    const [temptype, settemptype] = useState('');
    const [tempamount, settempamount] = useState(0);
    const [notify, setNotify] = useState({ isOpen: false, message: '', variant: 'filled', severity: 'error' });
    const arrOfferingTypes = [
        { "name": "Select", "value": "" },
        { "name": "Tithe", "value": "Tithe" },
        { "name": "Offering", "value": "Offering" },
        { "name": "Missionary", "value": "Missionary" },
        { "name": "Special Offering", "value": "Special Offering" },
        { "name": "Baptism Offering", "value": "Baptism Offering" },
        { "name": "Birthday Offering", "value": "Birthday Offering" },
        { "name": "Wedding Offering", "value": "Wedding Offering" },
        { "name": "Child Dedication Offering", "value": "Child Dedication Offering" },
        { "name": "Committed Offering", "value": "Committed Offering" },
        { "name": "Building Fund", "value": "Building Fund" }
    ]

    const sumbit = () => {
        fetch(`${url}/jehovanissi/api/accounting-fetch.php`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',

            },
            body: JSON.stringify({
                date: date
            })
        })
            .then(res => res.json())
            .then((result) => {
                if (result !== "No matches found on Record") {
                    setdata(result);
                    isDateset(true);
                    data.map((x, key) => {
                        setdata((currentPeople) => currentPeople.map((p, l) => l === key ? {
                            ...p,
                            OldType: p.Type,
                        } : p));
                    })
                }
                else {
                    setNotify({
                        ...notify,
                        isOpen:true,
                        message:"Input All Required"
                    })
                }


            }).catch((error) => {
                console.error(error);
            });
    }
    const submit2 = () => {
        fetch(`${url}/jehovanissi/api/accounting-fetch-denominations.php`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',

            },
            body: JSON.stringify({
                date: date
            })
        })
            .then(res => res.json())
            .then((result) => {
                if (result !== "No matches found on Record") {
                    setdata2(result[0]);
                }

            }).catch((error) => {
                console.error(error);
            });
    }





    let Total = isNaN(data.reduce((accumulator, item) => isNaN(accumulator) ? 0 : accumulator + parseFloat(item.Amount), 0)) ? 0 : data.reduce((accumulator, item) => isNaN(accumulator) ? 0 : accumulator + parseFloat(item.Amount), 0);

    let grandTotal = (1 * data2['One']) + (2 * data2['Two']) + (5 * data2['Five']) + (10 * data2['Ten']) + (20 * data2['Twenty']) + (50 * data2['Fifty']) + (100 * data2['Hundreds']) + (200 * data2['TwoHundreds']) + (500 * data2['FiveHundreds']) + (2000 * data2['TwoThousands']);

    let titheTotal = isNaN(data.reduce((accumulator, item) => item.Type === 'Tithe' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : data.reduce((accumulator, item) => item.Type === 'Tithe' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    let offeringTotal = isNaN(data.reduce((accumulator, item) => item.Type === 'Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : data.reduce((accumulator, item) => item.Type === 'Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    let missionaryTotal = isNaN(data.reduce((accumulator, item) => item.Type === 'Missionary' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : data.reduce((accumulator, item) => item.Type === 'Missionary' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    let specialofferingTotal = isNaN(data.reduce((accumulator, item) => item.Type === 'Special Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : data.reduce((accumulator, item) => item.Type === 'Special Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    let baptismofferingTotal = isNaN(data.reduce((accumulator, item) => item.Type === 'Baptism Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : data.reduce((accumulator, item) => item.Type === 'Baptism Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    let birthdayofferingTotal = isNaN(data.reduce((accumulator, item) => item.Type === 'Birthday Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : data.reduce((accumulator, item) => item.Type === 'Birthday Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    let weddingofferingTotal = isNaN(data.reduce((accumulator, item) => item.Type === 'Wedding Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : data.reduce((accumulator, item) => item.Type === 'Wedding Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    let childdedicationofferingTotal = isNaN(data.reduce((accumulator, item) => item.Type === 'Child Dedication Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : data.reduce((accumulator, item) => item.Type === 'Child Dedication Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    let committedofferingTotal = isNaN(data.reduce((accumulator, item) => item.Type === 'Committed Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : data.reduce((accumulator, item) => item.Type === 'Committed Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    let buildingfundTotal = isNaN(data.reduce((accumulator, item) => item.Type === 'Building Fund' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : data.reduce((accumulator, item) => item.Type === 'Building Fund' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    let churchOffering = grandTotal - Total;

    let name = data.map((k) => k.Name);

    let type = data.map((k) => k.Type);

    let oldtype = data.map((k) => k.OldType);

    let amount = data.map((k) => k.Amount);
    const [names, setnames] = useState([]);

    const [searchterm, setsearchterm] = useState();

    const results = !searchterm ? names : names.filter(person => person.toLowerCase().includes(searchterm.toLocaleLowerCase()));
    const searchapi = () => {
        fetch(`${url}/jehovanissi/api/searchname.php`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',

            },
        })
            .then(res => res.json())
            .then((result) => {
                setnames(result);
            }).catch((error) => {
                console.error(error);
            });
    }
    useEffect(() => {
        searchapi();
    }, [1])
    const confirm = () => {


        fetch(`${url}/jehovanissi/api/edit-accounting.php`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',

            },
            body: JSON.stringify({
                Name: name,
                Type: type,
                Amount: amount,
                OldType: oldtype,
                date: date,
                TwoThousands: data2['TwoThousands'],
                FiveHundreds: data2['FiveHundreds'],
                TwoHundreds: data2['TwoHundreds'],
                Hundreds: data2['Hundreds'],
                Fifty: data2['Fifty'],
                Twenty: data2['Twenty'],
                Ten: data2['Ten'],
                Five: data2['Five'],
                Two: data2['Two'],
                One: data2['One'],
                ChurchOffering: churchOffering,
                TitheTotal: titheTotal,
                OfferingTotal: offeringTotal,
                SpecialOfferingTotal: specialofferingTotal,
                BaptismofferingTotal: baptismofferingTotal,
                BirthdayofferingTotal: birthdayofferingTotal,
                WeddingofferingTotal: weddingofferingTotal,
                ChilddedicationofferingTotal: childdedicationofferingTotal,
                CommittedofferingTotal: committedofferingTotal,
                MissionaryTotal: missionaryTotal,
                BuildingFundTotal: buildingfundTotal,
                GrandTotal: grandTotal

            })
        })
            .then(res => res.json())
            .then((result) => {
                setNotify({
                    ...notify,
                    isOpen:true,
                    message:result
                })

            }).catch((error) => {
                console.error(error);
            });

    }
    const deleteRow = (delName, delType) => {
        fetch(`${url}/jehovanissi/api/accounting-delete-row.php`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',

            },
            body: JSON.stringify({
                date: date,
                name: delName,
                type: delType

            })
        })
            .then(res => res.json())

            .catch((error) => {
                console.error(error);
            });
    }
    const addnew = () => {
        fetch(`${url}/jehovanissi/api/accounting-add-new.php`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',

            },
            body: JSON.stringify({
                Name: tempname,
                date: date,
                Name: tempname,
                Type: temptype,
                Amount: tempamount

            })
        })
            .then(res => res.json())
            .catch((error) => {
                console.error(error);
            });
    }

    return (
        <Grid style={{ display: "grid", justifyContent: "center" }} >
            <AlertMessage
                notify={notify}
                setNotify={setNotify}
            />
            <Grid>
                <TextField type="date" onChange={(event) => setdate(event.target.value)} />
                <Button className="btn2" onClick={() => {
                    sumbit();
                    submit2();
                }} >Submit</Button>
            </Grid>
            <Grid style={{ display: dateset ? "block" : "none" }} >

                <Table>
                    <TableRow>
                        <TableHead>
                            <TableCell>
                                <Typography>
                                    REPORT ON {date}
                                </Typography>
                            </TableCell>
                        </TableHead>
                    </TableRow>



                    {data.map((p, key) => {
                        return (
                            <TableRow key={key} >
                                <TableCell>
                                    <p>{p.Name}</p>
                                </TableCell>


                                <TableCell>
                                    <Select value={p.Type}
                                        onChange={(event) => {
                                            const Type = event.target.value;
                                            setdata((currentPeople) => currentPeople.map((x, l) => l === key ? {
                                                ...x,
                                                Type
                                            } : x));

                                        }} >
                                        {arrOfferingTypes.map((val, key) => {
                                            return (
                                                <MenuItem
                                                    value={val.value}
                                                    key={key}
                                                >
                                                    {val.name}
                                                </MenuItem>
                                            )
                                        })}
                                    </Select>
                                </TableCell>
                                <TableCell><TextField onChange={(event) => {
                                    const Am = event.target.value;
                                    let patt = /[^0-9]/g;
                                    const Amount = parseFloat(Am.replace(patt, ''));
                                    setdata((currentPeople) => currentPeople.map((x, l) => l === key ? {
                                        ...x,
                                        Amount
                                    } : x));
                                }}
                                    value={p.Amount}
                                    type="number"
                                    placeholder="Enter Amount" /></TableCell>
                                <TableCell>
                                    <IconButton
                                        style={{ border: "none", cursor: "pointer" }}
                                        onClick={
                                            () => {
                                                setdata(currentPeople => currentPeople.filter((x, id) => id !== key));
                                                deleteRow(p.Name, p.Type);

                                            }
                                        }>
                                        <DeleteOutlineIcon />
                                    </IconButton>
                                </TableCell>

                            </TableRow>
                        )
                    })}


                    <TableRow>

                        <TableCell>
                            <IconButton
                                style={{ border: "none", cursor: "pointer" }}
                                onClick={() => {
                                    setclick(click + 1);

                                }}
                            >
                                <AddCircleOutline />
                            </IconButton>
                        </TableCell>

                    </TableRow>
                    <TableRow style={{ display: click === 0 ? "none" : "" }} >
                        <TableCell>
                            <Autocomplete
                                options={results}
                                renderInput={(params) =>
                                    <TextField
                                        {...params}
                                        onChange={(event) => {
                                            const str = event.target.value.split(' ');
                                            for (let i = 0; i < str.length; i++) {

                                                str[i] = str[i].charAt(0).toUpperCase() + str[i].substring(1);
                                            }
                                            const Name = str.join(' ');
                                            setsearchterm(Name);
                                            settempname(Name);
                                        }}
                                        value={tempname}
                                        placeholder="Enter Name"

                                    />
                                }
                            />


                        </TableCell>
                        <TableCell>
                            <Select
                                value={temptype}
                                onChange={(event) => {
                                    const Type = event.target.value;
                                    settemptype(Type);

                                }} >
                                {arrOfferingTypes.map((val, key) => {
                                    return (
                                        <MenuItem
                                            key={key}
                                            value={val.value}
                                        >
                                            {val.name}
                                        </MenuItem>
                                    )
                                })}
                            </Select>

                        </TableCell>
                        <TableCell>
                            <TextField
                                onChange={(event) => {
                                    const Am = event.target.value;
                                    let patt = /[^0-9]/g;
                                    const Amount = parseFloat(Am.replace(patt, ''));
                                    settempamount(Amount);
                                }}
                                value={tempamount}
                                type="number"
                                placeholder="Enter Amount" />
                        </TableCell>
                        <TableCell>
                            <Button
                                onClick={() => {
                                    setdata(currentPeople => [...currentPeople, {
                                        Name: tempname,
                                        Type: temptype,
                                        Amount: tempamount,
                                    }]);

                                    addnew();
                                    settempname('');
                                    settempamount(0);
                                    settemptype('');
                                }}
                            >
                                Confirm Adding
                            </Button>
                        </TableCell>

                    </TableRow>

                    <TableRow>
                        <TableCell></TableCell>
                        <TableCell>
                            <Typography>
                                Church Offering
                            </Typography>
                        </TableCell>
                        <TableCell>
                            {churchOffering}
                        </TableCell>

                    </TableRow>


                    <TableRow>
                        <TableCell></TableCell>
                        <TableCell>
                            <Typography>
                                Total
                            </Typography>
                        </TableCell>
                        <TableCell>
                            {Total}
                        </TableCell>

                    </TableRow>


                    <TableRow>
                        <TableCell></TableCell>
                        <TableCell>
                            Grand Total
                        </TableCell>
                        <TableCell>
                            {grandTotal}
                        </TableCell>

                    </TableRow>



                </Table>



                <Grid>
                    <Grid
                        item
                        container
                        direction={"row"}
                        alignItems={"center"}
                        margin={2}
                    >
                        <Typography>
                            2000 x
                        </Typography>
                        <TextField
                            value={data2['TwoThousands']}
                            onChange={(event) => { setdata2({ ...data2, TwoThousands: event.target.value }) }}
                        />
                        <Typography>
                            ={2000 * (data2['TwoThousands'])}
                        </Typography>
                    </Grid>
                    <Grid
                        item
                        container
                        direction={"row"}
                        alignItems={"center"}
                        margin={2}
                    >
                        <Typography>500 x</Typography>
                        <TextField
                            value={data2['FiveHundreds']}
                            onChange={(event) => setdata2({ ...data2, FiveHundreds: event.target.value })}
