import React, { useEffect, useState } from "react";
import { url } from "../Constants";
import { Grid, Button, FormControlLabel, RadioGroup, Radio, Autocomplete } from "@mui/material";
import { CFormInput, CFormSwitch } from "@coreui/react";


const Dataentry = () => {
    const [name, setName] = useState('');
    const [dob, setDob] = useState('');
    const [familyheads, setFamilyheads] = useState([]);
    const [validity, setvalidity] = useState(true);

    const [data, setData] = useState({
        "name": '',
        "dob": new Date(),
        "gender": '',
        "weddingdate": new Date().toLocaleDateString(),
        "emailid": '',
        "mobile": '',
        "address": '',
        "familyhead": '',
        "familyid": '',
        "selfid": '',
        "android": false,
        "baptized": false,
        "married": false

    })

    const str = dob.split('-');
    const today = new Date();
    const mon = today.getMonth();
    const year = today.getFullYear();
    let age;



    let k = year - 1;

    if (mon >= str[1]) {
        age = year - str[0];


    }
    else {
        age = k - str[0];

