                setopacity5(1);
                setborder5("red");
                setfontsize5("14px");
                setcolor5("red");
            }
            else {
                settop5("20px");
                setopacity5(1);
                setborder5("#ccc");
                setcolor5("#f70084");
                setfontsize5("14px");
            }


        }
    }
    const changefloat6 = () => {
        settop6("20px");
        setopacity6(1);
        setcolor6("#f70084");
        setfontsize6("14px");
    }

    const changetorest6 = () => {
        if (!address) {
            settop6("35px");
            setopacity6(0.5);
            setcolor6("black");
            setfontsize6("22px");
        }
        else {
            settop6("20px");
            setopacity6(1);
            setcolor6("#f70084");
            setfontsize6("14px");
        }
    }
    const changefloat7 = () => {
        settop7("20px");
        setopacity7(1);
        setcolor7("#f70084");
        setfontsize7("14px");
    }

    const changetorest7 = () => {
        if (!familyhead) {
            settop7("35px");
            setopacity7(0.5);
            setcolor7("black");
            setfontsize7("22px");
        }
        else {
            settop7("20px");
            setopacity7(1);
            setcolor7("#f70084");
            setfontsize7("14px");
        }

    }

    const addrecords = () => {
        if ((familyid && familyhead && address && selfid && name && gender && (!mobile.length < 10)) !== '') {
            setvalidity(true);
        }
        else {
            setvalidity(false);
        }

        if (validity) {

            let Familyid = familyid;
            let Selfid = selfid;
            let Name = name;
            let Dob = dob;
            let Age = age;
            let Gender = gender;
            let Married = married;
            let Weddingdate = weddingdate;
            let Emailid = emailid;
            let Mobile = mobile;
            let Address = address;
            let Familyhead = familyhead;
            let Baptized = baptized;
            let Android = android;


            fetch(`${url}/jehovanissi/backend/apis/member_details.php`, {
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({

                    name: Name,
                    dob: Dob,
                    age: Age,
                    gender: Gender,
                    married: Married,
                    weddingdate: Weddingdate,
                    emailid: Emailid,
                    mobile: Mobile,
                    address: Address,
                    familyhead: Familyhead,
                    baptized: Baptized,
                    android: Android,
                    familyid: Familyid,
                    selfid: Selfid
                })
            })
                .then(res => res.json())
                .then((result) => {
                    alert(result);
                    window.location.reload();
                }).catch((error) => {
                    console.error(error);
                });
        }
        else {
            alert("Input All required");

        }



    }

    const searchfamilyhead = () => {
        fetch(`${url}/jehovanissi/backend/apis/searchfamilyhead.php`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },

        })
            .then(res => res.json())
            .then((result) => {
                let filteredresult = [...new Set(result)];
                setFamilyheads(filteredresult);
            }).catch((error) => {
                console.error(error);
            });
    }


    return (
        <Grid>
            <TextField
                name="familyid"
                value={familyid}
                onChange={(event) => setFamilyid(event.target.value.toUpperCase())}
                placeholder="Family ID"
                label="Family ID"
            />

            <TextField
                name="selfid"
                value={selfid}
                placeholder="Self ID"
                label="Self ID"
            />
            <TextField
                name="name"
                value={name}
                onChange={(event) => {
                    const str = event.target.value.split(' ');
                    for (let i = 0; i < str.length; i++) {

                        str[i] = str[i].charAt(0).toUpperCase() + str[i].substring(1);
                    }

                    const Nameoff = str.join(' ');
                    setName(Nameoff)
                }}
                variant="outlined"
                placeholder="Enter Name"
                label="Enter Name"
            />
            <TextField
                type="date"
                name="dob"
                value={dob}
                onChange={(event) => setDob(event.target.value)}
                placeholder="Enter DOB"
                label="Enter DOB"
            />
            <Grid>
                <RadioGroup row aria-label="gender" name="row-radio-buttons-group">
                    <FormControlLabel
                        value="male"
                        name="gender"
                        label={"Male"}
                        control={<Radio />}
                        onChange={(event) => setGender(event.target.value)} />
                    <FormControlLabel
                        value="female"
                        name="gender"
                        label={"Female"}
                        control={<Radio />}
                        onChange={(event) => setGender(event.target.value)} />
                </RadioGroup>
                <RadioGroup row aria-label="martial-status" name="row-radio-buttons-group">
                    <FormControlLabel
                        value={true}
                        label={"Married"}
                        control={<Radio />}
                        onChange={(event) => { setMarried(event.target.value === 'true' ? event.target.value = true : event.target.value = false); setdisplay(event.target.value === "true" ? "block" : "none") }} />
                    <FormControlLabel
                        value={false}
                        control={<Radio />}
                        label="Unmarried"
                        onChange={(event) => { setMarried(event.target.value === 'false' ? event.target.value = false : event.target.value = true); setdisplay(event.target.value === "false" ? "none" : "block") }} />
                </RadioGroup>
            </Grid>
            <TextField style={{ display: display }}
                type="date"
                label="Wedding Date"
                defaultValue={"01/01/2020"}
                onChange={(event) => setWeddingdate(event.target.value)}
            />
            <TextField
                name="emailid"
                label="Email ID"
                onChange={(event) => setEmailid(event.target.value)}
                value={emailid}
            />
            <TextField
                style={{ borderColor: border5 }}
                name="mobile"
                label="Mobile"
                value={mobile}
                onChange={(event) => {
                    let patt = /[^0-9]/g;
                    const ph = event.target.value.replace(patt, '');
                    setMobile(ph.slice(0, 10));
                }} />

            <TextField
                name="address"
                label="Address"
                value={address}
                onChange={(event) => setAddress(event.target.value)}
            />
            <TextField
                name="familyhead"
                value={familyhead}
                label="Family Head"
                onChange={(event) => {
                    searchfamilyhead();
                    const str = event.target.value.split(' ');
                    for (let i = 0; i < str.length; i++) {

                        str[i] = str[i].charAt(0).toUpperCase() + str[i].substring(1);
                    }

                    const familyheadname = str.join(' ');

                    setFamilyhead(familyheadname)
                }}
                list="searchfhead"
            />
            <datalist id="searchfhead">
                {familyheads.map((k, key) => {
                    return (
                        <option key={key}>{k}</option>
                    )
                })}
            </datalist>
            <RadioGroup row aria-label="Baptizm" name="row-radio-buttons-group" >
                <FormControlLabel
                    control={<Radio />}
                    value={true}
                    label={"Baptized"}
                    onChange={(event) => setBaptized(event.target.value === 'true' ? event.target.value = true : event.target.value = false)}
                />
                <FormControlLabel
                    value={false}
                    control={<Radio />}
                    label={"Not Baptized"}
                    onChange={(event) => setBaptized(event.target.value === 'false' ? event.target.value = false : event.target.value = true)}
                />
            </RadioGroup>

            <RadioGroup row aria-label="mobile-type" name="row-radio-buttons-group">
                <FormControlLabel
                    label={"Android"}
                    value={true}
                    control={<Radio />}
                    onChange={(event) => setAndroid(event.target.value === 'true' ? event.target.value = true : event.target.value = false)} />
                <FormControlLabel
                    label={"Normal"}
                    value={false}
                    control={<Radio />}
                    onChange={(event) => setAndroid(event.target.value === 'false' ? event.target.value = false : event.target.value = true)} />
            </RadioGroup>

            <Button
                variant="contained"
                onClick={addrecords} >
                Add to records</Button>
        </Grid>
    )
}

export default Dataentry;
