import React, { useState } from 'react';
import { url } from '../Constants';
import { Grid, TextField, Button, IconButton } from '@mui/material';
import DeleteSweepIcon from '@mui/icons-material/DeleteSweep';

const YoutubeID = () => {
    const [links, setlinks] = useState([{ sno: 1, id: '' }]);
    const [count, setcount] = useState(2);

    let id = links.map((k) => k.id);

    const submit = () => {
        fetch(`${url}/jehovanissi/backend/apis/youtubeid.php`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',

            },
            body: JSON.stringify({
                id: id

            })
        })
            .then(res => res.json())
            .then((result) => {
                alert(result);
                window.location.reload();
            }).catch((error) => {
                console.error(error);
            });

    }




    return (
        <Grid
            container
            direction={"column"}
        >
            <Button className="btn" onClick={() => {
                setlinks(currentPeople => [...currentPeople, {
                    sno: count,
                    id: ''
                }]); setcount(count + 1);
            }} >Add</Button>
            {links.map((k) => {
                return (
                    <Grid
                        item
                        key={k.sno}
                        direction={"column"}
                        justifyContent={"center"}
                        justifyItems={"center"}
                        alignContent={"center"}
                        alignItems={"center"}
                    >
                        <TextField 
                        margin="dense"
                        onChange={(event) => {
                            const temp = event.target.value;
                            const id = temp.slice(17);
                            setlinks((currentPeople) => currentPeople.map(x => x.sno === k.sno ? {
                                ...x,
                                id
                            } : x));
                        }} placeholder="Enter the link" />

                        <IconButton className="btn" variant="contained" onClick={
                            () => {
                                setlinks(currentPeople => currentPeople.filter(x => x.sno !== k.sno)); setcount(count - 1)
                            }
                        }><DeleteSweepIcon />
                        </IconButton>

                    </Grid>
                )
            })}

            <Grid
                item
                justifyContent={"center"}
                justifyItems={"center"}
                alignContent={"center"}
                alignItems={"center"}
            >
                <Button className="btn" onClick={() => submit()}>Submit</Button>
            </Grid>
        </Grid>
    )
}

export default YoutubeID;
