    if (name[0] && dateofofferings && (amount !== 0) && churchOffering >= 0) {
      fetch(`http://${url}/jehovanissi/backend/apis/accounting.php`, {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',

        },
        body: JSON.stringify({
          name: name,
          type: type,
          amount: amount,
          dateofofferings: dateofofferings,
          churchOffering: churchOffering,
          titheTotal: titheTotal,
          grandTotal: grandTotal,
          offeringTotal: offeringTotal,
          missionaryTotal: missionaryTotal,
          specialofferingTotal: specialofferingTotal,
          buildingfundTotal: buildingfundTotal,
          baptismofferingTotal: baptismofferingTotal,
          birthdayofferingTotal: birthdayofferingTotal,
          weddingofferingTotal: weddingofferingTotal,
          childdedicationofferingTotal: childdedicationofferingTotal,
          committedofferingTotal: committedofferingTotal,
          twothousands: twothousands,
          twohundreds: twohundreds,
          fivehundreds: fivehundreds,
          hundreds: hundreds,
          fiftys: fiftys,
          twentys: twentys,
          tens: tens,
          fives: fives,
          twos: twos,
          ones: ones

        })
      })
        .then(res => res.json())
        .then((result) => {
          alert(result);
          window.location.reload();
        }).catch((error) => {
          console.error(error);
        });

    }


    else {
      alert("Input all required and check for mistakes");
    }
  }
  const searchapi = () => {
    fetch(`${url}/jehovanissi/backend/apis/searchname.php`, {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',

      },
    })
      .then(res => res.json())
      .then((result) => {
        setnames(result);
      }).catch((error) => {
        console.error(error);
      });
  }


  return (
    <Grid
      container
      justifyContent={"space-evenly"}
    >
      <Grid

      >
        {people.map((p) => {
          return (
            <Grid key={p.id}>
              <TextField
                margin="dense"
                onChange={(event) => {
                  searchapi();
                  const str = event.target.value.split(' ');
                  for (let i = 0; i < str.length; i++) {

                    str[i] = str[i].charAt(0).toUpperCase() + str[i].substring(1);
                  }

                  const Name = str.join(' ');
                  setsearchterm(Name);
                  setPeople((currentPeople) => currentPeople.map(x => x.id === p.id ? {
                    ...x,
                    Name
                  } : x));
                }} value={p.Name}
                list="namelist"
                placeholder="Enter Name"

              />
              <datalist id="namelist">
                {results.map((k, key) => {
                  return (
                    <option key={key} >{k}</option>
                  )
                })}
              </datalist>


              <Select onChange={(event) => {
                const type = event.target.value;
                setPeople((currentPeople) => currentPeople.map(x => x.id === p.id ? {
                  ...x,
                  type
                } : x));

              }}
                style={{ margin: 9 }}
              >
                <MenuItem className="options">Select </MenuItem>
                <MenuItem className="options" value="Tithe">Tithe</MenuItem>
                <MenuItem className="options" value="Offering">Offering</MenuItem>
                <MenuItem className="options" value="Missionary">Missionary</MenuItem>
                <MenuItem className="options" value="Special Offering">Special Offering</MenuItem>
                <MenuItem className="options" value="Baptism Offering">Baptism Offering</MenuItem>
                <MenuItem className="options" value="Birthday Offering">Birthday Offering</MenuItem>
                <MenuItem className="options" value="Wedding Offering">Wedding Offering</MenuItem>
                <MenuItem className="options" value="Child Dedication Offering">Child Dedication Offering</MenuItem>
                <MenuItem className="options" value="Committed Offering">Committed Offering</MenuItem>
                <MenuItem className="options" value="Building Fund">Building Fund</MenuItem>
              </Select>

              <TextField
                margin="dense"
                onChange={(event) => {
                  const Am = event.target.value;
                  let patt = /[^0-9]/g;
                  const Amount = parseFloat(Am.replace(patt, ''));
                  setPeople((currentPeople) => currentPeople.map(x => x.id === p.id ? {
                    ...x,
                    Amount
                  } : x));
                }}
                value={p.Amount}
                type="number"
                placeholder="Enter Amount" />

              <IconButton
                onClick={
                  () => {
                    setPeople(currentPeople => currentPeople.filter(x => x.id !== p.id)); setCount(count - 1)
                  }
                }><DeleteOutlineIcon/></IconButton>
            </Grid>
          )

        })}
        <IconButton
          onClick={() => {
            setPeople(currentPeople => [...currentPeople, {
              id: count,
              Name: '',
              type: '',
              Amount: '',
            }]); setCount(count + 1)
          }}
        ><AddCircleOutlineIcon /></IconButton>

        <Typography>Total:{Total}</Typography>
        <Typography>Tithe Total:{JSON.stringify(titheTotal)}</Typography>
        <Typography>Offering Total:{JSON.stringify(offeringTotal)}</Typography>
        <Typography>Special Offering Total:{JSON.stringify(specialofferingTotal)}</Typography>
        <Typography>Missionary Total:{JSON.stringify(missionaryTotal)}</Typography>
        <Typography>Building Fund Total:{JSON.stringify(buildingfundTotal)}</Typography>


        <p>Church Offering:{isNaN(churchOffering) ? 0 : churchOffering}</p>
        <p>Grand Total:{isNaN(grandTotal) ? 0 : grandTotal}</p>
        <button onClick={submit} style={{ justifySelf: "center", paddingLeft: 90, paddingRight: 90, paddingTop: 25, paddingBottom: 25 }} >Submit</button>
      </Grid>
      <Grid>
        <TextField
          value={dateofofferings}
          onChange={(event) => setdateofofferings(event.target.value)}
          type="date" />
        <Button
          onClick={() => setdateofofferings(today)}
        >
          Click if date is todays date
        </Button>

        {offeringsAsObject.map((k,key)=>{
          return(
            <>
            <Typography>{k.price} x </Typography>
            <TextField
              onChange={(e)=>{
                setOfferings(cp=>cp.map((l,lkey)=>lkey===key?
                  {
                    ...l,
                    cnt:parseInt(e.target.value),
                    tot:parseInt(e.target.value)*k.price
                  }
                :l))
              }}  
            /> 
            <Typography>{isNaN(k.tot)?0:k.tot}</Typography>
            </>
          )
        })}
        <p>{offeringsAsObject[0]?.reduce((acc,item)=>acc+item.tot)}</p>

        {/* <p>2000 x <input min="0" onChange={(event) => settwothousands(event.target.valueAsNumber)} type="number" /><span>={isNaN(2000 * twothousands) ? 0 : 2000 * twothousands}</span></p>
        <p>500 x <input min="0" onChange={(event) => setfivehunndreds(event.target.valueAsNumber)} type="number" /><span>={isNaN(500 * fivehundreds) ? 0 : 500 * fivehundreds}</span></p>
        <p>200 x <input min="0" onChange={(event) => settwohundreds(event.target.valueAsNumber)} type="number" /><span>={isNaN(200 * twohundreds) ? 0 : 200 * twohundreds}</span></p>
        <p>100 x <input min="0" onChange={(event) => sethundreds(event.target.valueAsNumber)} type="number" /><span>={isNaN(100 * hundreds) ? 0 : 100 * hundreds}</span></p>
        <p>50 x <input min="0" onChange={(event) => setfiftys(event.target.valueAsNumber)} type="number" /><span>={isNaN(50 * fiftys) ? 0 : 50 * fiftys}</span></p>
        <p>20 x <input min="0" onChange={(event) => settwentys(event.target.valueAsNumber)} type="number" /><span>={isNaN(20 * twentys) ? 0 : 20 * twentys}</span></p>
        <p>10 x <input min="0" onChange={(event) => settens(event.target.valueAsNumber)} type="number" /><span>={isNaN(10 * tens) ? 0 : 10 * tens}</span></p>
        <p>5 x <input min="0" onChange={(event) => setfives(event.target.valueAsNumber)} type="number" /><span>={isNaN(5 * fives) ? 0 : 5 * fives}</span></p>
        <p>2 x <input min="0" onChange={(event) => settwos(event.target.valueAsNumber)} type="number" /><span>={isNaN(2 * twos) ? 0 : 2 * twos}</span></p>
        <p>1 x <input min="0" onChange={(event) => setones(event.target.valueAsNumber)} type="number" /><span>={isNaN(1 * ones) ? 0 : 1 * ones}</span></p> */}




      </Grid>

    </Grid>

  )
