import React, { useEffect, useState } from 'react'
import { url } from '../Constants'
import { Grid, Button, FormControlLabel, RadioGroup, Radio, Autocomplete, formLabelClasses } from '@mui/material'
import { CButton, CCol, CForm, CFormFeedback, CFormInput, CFormLabel, CFormSwitch, CFormTextarea } from '@coreui/react'

const Dataentry = () => {
  const [name, setName] = useState('')
  const [dob, setDob] = useState('')
  const [familyheads, setFamilyheads] = useState([])
  const [validity, setvalidity] = useState(true)

  const [data, setData] = useState({
    name: '',
    dob: new Date(),
    gender: 'male',
    weddingdate: new Date().toLocaleDateString(),
    emailid: '',
    mobile: '',
    address: '',
    familyhead: '',
    familyid: '',
    selfid: '',
    android: false,
    baptized: false,
    married: false,
  })

  const str = dob.split('-')
  const today = new Date()
  const mon = today.getMonth()
  const year = today.getFullYear()
  let age

  let k = year - 1

  if (mon >= str[1]) {
    age = year - str[0]
  } else {
    age = k - str[0]
  }

  const [validated, setValidated] = useState(false)

  const handleSubmit = (event) => {
    const form = event.currentTarget

    if (form.checkValidity() === false) {
      event.preventDefault()

      event.stopPropagation()
    }
    else {
      addrecords();
    }

    setValidated(true)
  }

  const addrecords = () => {
    if (validity) {
      let Familyid = data.familyid
      let Selfid = data.selfid
      let Name = data.name
      let Dob = data.dob
      let Age = age
      let Gender = data.gender
      let Married = data.married
      let Weddingdate = data.weddingdate
      let Emailid = data.emailid
      let Mobile = data.mobile
      let Address = data.address
      let Familyhead = data.familyhead
      let Baptized = data.baptized
      let Android = data.android

      fetch(`${url}/jehovanissi/api/member_details.php`, {
        method: 'POST',
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: Name,
          dob: Dob,
          age: Age,
          gender: Gender,
          married: Married,
          weddingdate: Weddingdate,
          emailid: Emailid,
          mobile: Mobile,
          address: Address,
          familyhead: Familyhead,
          baptized: Baptized,
          android: Android,
          familyid: Familyid,
          selfid: Selfid,
        }),
      })
        .then((res) => res.json())
        .then((result) => {
          alert(result)
          console.log(result)
        })
        .catch((error) => {
          console.error(error)
        })
    } else {
      alert('Input All required')
    }
  }

