import AddCircleOutline from "@mui/icons-material/AddCircleOutline";
import DeleteOutlineIcon from '@mui/icons-material/DeleteOutline';
import { Autocomplete, Button, Grid, IconButton, MenuItem, Select, Table, TableCell, TableHead, TableRow, TextField, Typography } from "@mui/material";
import React, { useEffect, useState } from "react";
import { url } from "../../Constants";
import { AlertMessage } from "../Support/AlertMessage";

const Editaccounting = () => {
    const [date, setdate] = useState('');
    const [dateset, isDateset] = useState(false);
    const [report, setOfferingReport] = useState([]);
    const [denominationsAndTotals, setDenominationsandTotals] = useState({});
    const [click, setclick] = useState(0);
    const [tempname, settempname] = useState('');
    const [temptype, settemptype] = useState('');
    const [tempamount, settempamount] = useState(0);
    const [notify, setNotify] = useState({ isOpen: false, message: '', variant: 'filled', severity: 'error' });
    const arrOfferingTypes = [
        { "name": "Select", "value": "" },
        { "name": "Tithe", "value": "Tithe" },
        { "name": "Offering", "value": "Offering" },
        { "name": "Missionary", "value": "Missionary" },
        { "name": "Special Offering", "value": "Special Offering" },
        { "name": "Baptism Offering", "value": "Baptism Offering" },
        { "name": "Birthday Offering", "value": "Birthday Offering" },
        { "name": "Wedding Offering", "value": "Wedding Offering" },
        { "name": "Child Dedication Offering", "value": "Child Dedication Offering" },
        { "name": "Committed Offering", "value": "Committed Offering" },
        { "name": "Building Fund", "value": "Building Fund" }
    ]

    const fetchOfferingAndDenominationsByDate = () => {
        fetch(`${url}/Church-management-api/api/accounting-fetch.php`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',

            },
            body: JSON.stringify({
                date: date
            })
        })
            .then(res => res.json())
            .then((result) => {
                if (result.status !== 0) {
                    // setOfferingReport(result.message[0]);
                    console.log(result.message[0],"REPORT");
                    setDenominationsandTotals(result.message[1]);
                    isDateset(true);
                    report.map((x, key) => {
                        setOfferingReport((currentPeople) => currentPeople.map((p, l) => l === key ? {
                            ...p,
                            OldType: p.Type,
                        } : p));
                    })
                }
                else {
                    setNotify({
                        ...notify,
                        isOpen:true,
                        message:result.message
                    })
                }


            }).catch((error) => {
                console.error(error);
            });
    }





    // let Total = isNaN(report.reduce((accumulator, item) => isNaN(accumulator) ? 0 : accumulator + parseFloat(item.Amount), 0)) ? 0 : report.reduce((accumulator, item) => isNaN(accumulator) ? 0 : accumulator + parseFloat(item.Amount), 0);

    // let grandTotal = (1 * denominationsAndTotals['One']) + (2 * denominationsAndTotals['Two']) + (5 * denominationsAndTotals['Five']) + (10 * denominationsAndTotals['Ten']) + (20 * denominationsAndTotals['Twenty']) + (50 * denominationsAndTotals['Fifty']) + (100 * denominationsAndTotals['Hundreds']) + (200 * denominationsAndTotals['TwoHundreds']) + (500 * denominationsAndTotals['FiveHundreds']) + (2000 * denominationsAndTotals['TwoThousands']);

    // let titheTotal = isNaN(report.reduce((accumulator, item) => item.Type === 'Tithe' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : report.reduce((accumulator, item) => item.Type === 'Tithe' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    // let offeringTotal = isNaN(report.reduce((accumulator, item) => item.Type === 'Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : report.reduce((accumulator, item) => item.Type === 'Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    // let missionaryTotal = isNaN(report.reduce((accumulator, item) => item.Type === 'Missionary' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : report.reduce((accumulator, item) => item.Type === 'Missionary' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    // let specialofferingTotal = isNaN(report.reduce((accumulator, item) => item.Type === 'Special Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : report.reduce((accumulator, item) => item.Type === 'Special Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    // let baptismofferingTotal = isNaN(report.reduce((accumulator, item) => item.Type === 'Baptism Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : report.reduce((accumulator, item) => item.Type === 'Baptism Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    // let birthdayofferingTotal = isNaN(report.reduce((accumulator, item) => item.Type === 'Birthday Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : report.reduce((accumulator, item) => item.Type === 'Birthday Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    // let weddingofferingTotal = isNaN(report.reduce((accumulator, item) => item.Type === 'Wedding Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : report.reduce((accumulator, item) => item.Type === 'Wedding Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    // let childdedicationofferingTotal = isNaN(report.reduce((accumulator, item) => item.Type === 'Child Dedication Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : report.reduce((accumulator, item) => item.Type === 'Child Dedication Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    // let committedofferingTotal = isNaN(report.reduce((accumulator, item) => item.Type === 'Committed Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : report.reduce((accumulator, item) => item.Type === 'Committed Offering' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    // let buildingfundTotal = isNaN(report.reduce((accumulator, item) => item.Type === 'Building Fund' ? accumulator + parseFloat(item.Amount) : accumulator, 0)) ? 0 : report.reduce((accumulator, item) => item.Type === 'Building Fund' ? accumulator + parseFloat(item.Amount) : accumulator, 0);

    // let churchOffering = grandTotal - Total;

    let name = report.map((k) => k.Name);

    let type = report.map((k) => k.Type);

    let oldtype = report.map((k) => k.OldType);

    let amount = report.map((k) => k.Amount);
    const [names, setnames] = useState([]);

    const [searchterm, setsearchterm] = useState();

    const results = !searchterm ? names : names.filter(person => person.toLowerCase().includes(searchterm.toLocaleLowerCase()));
    const searchapi = () => {
        fetch(`${url}/Church-management-api/api/searchname.php`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',

            },
        })
            .then(res => res.json())
            .then((result) => {
                setnames(result);
            }).catch((error) => {
                console.error(error);
            });
    }
    useEffect(() => {
        searchapi();
    }, [1])
    const confirm = () => {


        fetch(`${url}/Church-management-api/api/edit-accounting.php`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',

            },
            body: JSON.stringify({
                Name: name,
                Type: type,
                Amount: amount,
                OldType: oldtype,
                date: date,
                TwoThousands: denominationsAndTotals['TwoThousands'],
                FiveHundreds: denominationsAndTotals['FiveHundreds'],
                TwoHundreds: denominationsAndTotals['TwoHundreds'],
                Hundreds: denominationsAndTotals['Hundreds'],
                Fifty: denominationsAndTotals['Fifty'],
                Twenty: denominationsAndTotals['Twenty'],
                Ten: denominationsAndTotals['Ten'],
                Five: denominationsAndTotals['Five'],
                Two: denominationsAndTotals['Two'],
                One: denominationsAndTotals['One'],
                ChurchOffering: churchOffering,
                TitheTotal: titheTotal,
                OfferingTotal: offeringTotal,
                SpecialOfferingTotal: specialofferingTotal,
                BaptismofferingTotal: baptismofferingTotal,
                BirthdayofferingTotal: birthdayofferingTotal,
                WeddingofferingTotal: weddingofferingTotal,
                ChilddedicationofferingTotal: childdedicationofferingTotal,
                CommittedofferingTotal: committedofferingTotal,
                MissionaryTotal: missionaryTotal,
                BuildingFundTotal: buildingfundTotal,
                GrandTotal: grandTotal

            })
        })
            .then(res => res.json())
            .then((result) => {
                setNotify({
                    ...notify,
                    isOpen:true,
                    message:result
                })

            }).catch((error) => {
                console.error(error);
            });

    }
    const deleteRow = (delName, delType) => {
        fetch(`${url}/Church-management-api/api/accounting-delete-row.php`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',

            },
            body: JSON.stringify({
                date: date,
                name: delName,
                type: delType

            })
        })
            .then(res => res.json())

            .catch((error) => {
                console.error(error);
            });
    }
    const addnew = () => {
        fetch(`${url}/Church-management-api/api/accounting-add-new.php`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',

            },
            body: JSON.stringify({
                Name: tempname,
                date: date,
                Name: tempname,
                Type: temptype,
                Amount: tempamount

            })
        })
            .then(res => res.json())
            .catch((error) => {
                console.error(error);
            });
    }

    return (
        <Grid style={{ display: "grid", justifyContent: "center" }} >
            <AlertMessage
                notify={notify}
                setNotify={setNotify}
            />
            <Grid>
                <TextField type="date" onChange={(event) => setdate(event.target.value)} />
                <Button className="btn2" onClick={() => {
                    fetchOfferingAndDenominationsByDate();
                }} >Submit</Button>
            </Grid>
            <Grid style={{ display: dateset ? "block" : "none" }} >

                <Table>
                    <TableRow>
                        <TableHead>
                            <TableCell>
                                <Typography>
                                    REPORT ON {date}
                                </Typography>
                            </TableCell>
                        </TableHead>
                    </TableRow>



                    {report.map((p, key) => {
                        return (
                            <TableRow key={key} >
                                <TableCell>
                                    <p>{p.Name}</p>
                                </TableCell>


                                <TableCell>
                                    <Select value={p.Type}
                                        onChange={(event) => {
                                            const Type = event.target.value;
                                            setOfferingReport((currentPeople) => currentPeople.map((x, l) => l === key ? {
                                                ...x,
                                                Type
                                            } : x));

                                        }} >
                                        {arrOfferingTypes.map((val, key) => {
                                            return (
                                                <MenuItem
                                                    value={val.value}
                                                    key={key}
                                                >
                                                    {val.name}
                                                </MenuItem>
                                            )
                                        })}
                                    </Select>
                                </TableCell>
                                <TableCell><TextField onChange={(event) => {
                                    const Am = event.target.value;
                                    let patt = /[^0-9]/g;
                                    const Amount = parseFloat(Am.replace(patt, ''));
                                    setOfferingReport((currentPeople) => currentPeople.map((x, l) => l === key ? {
                                        ...x,
                                        Amount
                                    } : x));
                                }}
                                    value={p.Amount}
                                    type="number"
                                    placeholder="Enter Amount" /></TableCell>
                                <TableCell>
                                    <IconButton
                                        style={{ border: "none", cursor: "pointer" }}
                                        onClick={
                                            () => {
                                                setOfferingReport(currentPeople => currentPeople.filter((x, id) => id !== key));
                                                deleteRow(p.Name, p.Type);

                                            }
                                        }>
                                        <DeleteOutlineIcon />
                                    </IconButton>
                                </TableCell>

                            </TableRow>
                        )
                    })}


                    <TableRow>

                        <TableCell>
                            <IconButton
                                style={{ border: "none", cursor: "pointer" }}
                                onClick={() => {
                                    setclick(click + 1);

                                }}
                            >
                                <AddCircleOutline />
                            </IconButton>
                        </TableCell>

                    </TableRow>
                    <TableRow style={{ display: click === 0 ? "none" : "" }} >
                        <TableCell>
                            <Autocomplete
                                options={results}
                                renderInput={(params) =>
                                    <TextField
                                        {...params}
                                        onChange={(event) => {
                                            const str = event.target.value.split(' ');
                                            for (let i = 0; i < str.length; i++) {

                                                str[i] = str[i].charAt(0).toUpperCase() + str[i].substring(1);
                                            }
                                            const Name = str.join(' ');
                                            setsearchterm(Name);
                                            settempname(Name);
                                        }}
                                        value={tempname}
                                        placeholder="Enter Name"

                                    />
                                }
                            />


                        </TableCell>
                        <TableCell>
                            <Select
                                value={temptype}
                                onChange={(event) => {
                                    const Type = event.target.value;
                                    settemptype(Type);

                                }} >
                                {arrOfferingTypes.map((val, key) => {
                                    return (
                                        <MenuItem
                                            key={key}
                                            value={val.value}
                                        >
                                            {val.name}
                                        </MenuItem>
                                    )
                                })}
                            </Select>

                        </TableCell>
                        <TableCell>
                            <TextField
                                onChange={(event) => {
                                    const Am = event.target.value;
                                    let patt = /[^0-9]/g;
                                    const Amount = parseFloat(Am.replace(patt, ''));
                                    settempamount(Amount);
                                }}
                                value={tempamount}
                                type="number"
                                placeholder="Enter Amount" />
                        </TableCell>
                        <TableCell>
                            <Button
                                onClick={() => {
                                    setOfferingReport(currentPeople => [...currentPeople, {
                                        Name: tempname,
                                        Type: temptype,
                                        Amount: tempamount,
                                    }]);

                                    addnew();
                                    settempname('');
                                    settempamount(0);
                                    settemptype('');
                                }}
                            >
                                Confirm Adding
                            </Button>
                        </TableCell>

                    </TableRow>

                    <TableRow>
                        <TableCell></TableCell>
                        <TableCell>
                            <Typography>
                                Church Offering
                            </Typography>
                        </TableCell>
                        <TableCell>
                            {churchOffering}
                        </TableCell>

                    </TableRow>


                    <TableRow>
                        <TableCell></TableCell>
                        <TableCell>
                            <Typography>
                                Total
                            </Typography>
                        </TableCell>
                        <TableCell>
                            {Total}
                        </TableCell>

                    </TableRow>


                    <TableRow>
                        <TableCell></TableCell>
                        <TableCell>
                            Grand Total
                        </TableCell>
                        <TableCell>
                            {grandTotal}
                        </TableCell>

                    </TableRow>



                </Table>



                <Grid>
                    <Grid
                        item
                        container
                        direction={"row"}
                        alignItems={"center"}
                        margin={2}
                    >
                        <Typography>
                            2000 x
                        </Typography>
                        <TextField
                            value={denominationsAndTotals['TwoThousands']}
                            onChange={(event) => { setDenominationsandTotals({ ...denominationsAndTotals, TwoThousands: event.target.value }) }}
                        />
                        <Typography>
                            ={2000 * (denominationsAndTotals['TwoThousands'])}
                        </Typography>
                    </Grid>
                    <Grid
                        item
                        container
                        direction={"row"}
                        alignItems={"center"}
                        margin={2}
                    >
