import React, { useEffect, useState } from "react";
import { url } from "../Constants";
import { Grid, TextField, Button, FormControlLabel, RadioGroup, Radio, Autocomplete } from "@mui/material";

const Dataentry = () => {
    const [name, setName] = useState('');
    const [dob, setDob] = useState('');
    const [familyheads, setFamilyheads] = useState([]);
    const [validity, setvalidity] = useState(true);

    const [data, setData] = useState({
        "name": '',
        "dob": '',
        "gender": '',
        "weddingdate": '',
        "emailid": '',
        "mobile": '',
        "address": '',
        "familyhead": '',
        "familyid": '',
        "selfid": '',
        "android": false,
        "baptized": false,
        "married": false

    })

    const str = dob.split('-');
    const today = new Date();
    const mon = today.getMonth();
    const year = today.getFullYear();
    let age;



    let k = year - 1;

    if (mon >= str[1]) {
        age = year - str[0];


    }
    else {
        age = k - str[0];

    }

    const validation = () =>{
        if ((data.familyid && data.familyhead && data.address && data.selfid && data.name && data.gender && (!data.mobile.length < 10)) !== '') {
            setvalidity(true);
            return true;
        }
        else {
           setvalidity(false);
           return false;
        }
    }

    const addrecords = () => {
        if (validity) {

            let Familyid = data.familyid;
            let Selfid = data.selfid;
            let Name = data.name;
            let Dob = data.dob;
            let Age = age;
            let Gender = data.gender;
            let Married = data.married;
            let Weddingdate = data.weddingdate;
            let Emailid = data.emailid;
            let Mobile = data.mobile;
            let Address = data.address;
            let Familyhead = data.familyhead;
            let Baptized = data.baptized;
            let Android = data.android;


            fetch(`${url}/jehovanissi/api/member_details.php`, {
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    name: Name,
                    dob: Dob,
                    age: Age,
                    gender: Gender,
                    married: Married,
                    weddingdate: Weddingdate,
                    emailid: Emailid,
                    mobile: Mobile,
                    address: Address,
                    familyhead: Familyhead,
                    baptized: Baptized,
                    android: Android,
                    familyid: Familyid,
                    selfid: Selfid
                })
            })
                .then(res => res.json())
                .then((result) => {
                    alert(result);
                    console.log(result);
                }).catch((error) => {
                    console.error(error);
                });
        }
        else {
            alert("Input All required");

        }



    }

    const searchfamilyhead = () => {
        fetch(`${url}/jehovanissi/api/searchfamilyhead.php`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },

        })
            .then(res => res.json())
            .then((result) => {
                let filteredresult = [...new Set(result)];
                setFamilyheads(filteredresult);
            }).catch((error) => {
                console.error(error);
            });
    }
    useEffect(() => {
        searchfamilyhead();
    }, [])

    return (
        <Grid
            direction="column"
            alignItems={"stretch"}
            justifyItems={"stretch"}
            container
        >
            <Grid
                direction={"column"}
                alignItems={"center"}
                justifyItems={"stretch"}
                container
                item
            >
                <Grid>
                    <TextField
                        style={{ width: 300, marginRight: 15 }}
                        name="familyid"
                        value={data.familyid}
                        margin="dense"
                        variant="standard"
                        onChange={(event) => {
                            setData({ ...data, familyid: event.target.value.toUpperCase() })
                        }}
                        placeholder="Family ID"
                        label="Family ID"
                    />
                    <TextField
                        name="selfid"
                        style={{ width: 300, marginRight: 15 }}
                        value={data.selfid}
                        margin="dense"
                        variant="standard"
                        onFocus={() => setData({ ...data, selfid: data.familyid.slice(3) })}
                        placeholder="Self ID"
                        label="Self ID"
                    />
                </Grid>
                <Grid>
                    <TextField
                        name="name"
                        value={data.name}
                        style={{ width: 300, marginRight: 15 }}
                        margin="dense"
                        variant="standard"
                        onChange={(event) => {
                            const str = event.target.value.split(' ');
                            for (let i = 0; i < str.length; i++) {

                                str[i] = str[i].charAt(0).toUpperCase() + str[i].substring(1);
                            }
                            const Nameoff = str.join(' ');
                            setData({ ...data, name: Nameoff })
                        }}
                        placeholder="Enter Name"
                        label="Enter Name"
                    />
                    <TextField
                        type="date"
                        name="dob"
                        style={{ width: 300, marginRight: 15 }}
                        margin="dense"
                        value={data.dob}
                        variant="standard"
                        onChange={(event) => setData({ ...data, dob: event.target.value })}
                        placeholder="Enter DOB"
                        label="Enter DOB"
                    />
                </Grid>
                <RadioGroup
                    row
                    aria-label="gender"
                    name="row-radio-buttons-group"
                    style={{
                        width: 300,
                        justifyContent: "space-evenly"
                    }}
                >
                    <FormControlLabel
                        value="male"
                        name="gender"
                        label={"Male"}
                        control={<Radio />}
                        onChange={(event) => setData({ ...data, gender: event.target.value })} />
                    <FormControlLabel
                        value="female"
                        name="gender"
                        label={"Female"}
                        control={<Radio />}
                        onChange={(event) => setData({ ...data, gender: event.target.value })} />
                </RadioGroup>
                <RadioGroup
                    row
                    aria-label="martial-status"
                    name="row-radio-buttons-group"
                    style={{
                        width: 300,
                        justifyContent: "space-evenly"
                    }}
                >
                    <FormControlLabel
                        value={true}
                        label={"Married"}
                        control={<Radio />}
                        onChange={(event) => { setData({ ...data, married: event.target.value }) }} />
                    <FormControlLabel
                        value={false}
                        control={<Radio />}
                        label="Unmarried"
                        onChange={(event) => { setData({ ...data, married: event.target.value }) }} />
                </RadioGroup>

                <TextField
                    type="date"
                    label="Wedding Date"
                    style={{ width: 300, marginRight: 15 }}
                    margin="dense"
                    variant="standard"
                    value={data.weddingdate}
                    onChange={(event) => setData({ ...data, weddingdate: event.target.value })}
                />
                <Grid>
                    <TextField
                        name="emailid"
                        margin="dense"
                        label="Email ID"
                        style={{ width: 300, marginRight: 15 }}
                        variant="standard"
                        onChange={(event) => setData({ ...data, emailid: event.target.value })}
                        value={data.emailid}
                    />

                    <TextField
                        name="mobile"
                        style={{ width: 300, marginRight: 15 }}
                        label="Mobile"
                        margin="dense"
                        variant="standard"
                        value={data.mobile}
                        onChange={(event) => {
                            let patt = /[^0-9]/g;
                            const ph = event.target.value.replace(patt, '');
                            setData({ ...data, mobile: ph.slice(0, 10) });
                        }} />
                </Grid>

                <Grid>
                    <TextField
                        name="address"
                        label="Address"
                        style={{ width: 300, marginRight: 15 }}
                        margin="dense"
                        variant="standard"
                        value={data.address}
                        onChange={(event) => {
                            const str = event.target.value.split(',');
                            for (let i = 0; i < str.length; i++) {
                                str[i] = str[i].charAt(0).toUpperCase() + str[i].substring(1);
                            }
                            const address = str.join(',');
                            setData({ ...data, address: address })
                        }}
                    />
                    <Autocomplete
                        options={familyheads}
                        renderInput={(params) =>
                            <TextField
                                {...params}
                                name="familyhead"
                                value={data.familyhead}
                                margin="dense"
                                variant="standard"
                                label="Family Head"
                                onChange={(event) => {
                                    const str = event.target.value.split(' ');
                                    for (let i = 0; i < str.length; i++) {
                                        str[i] = str[i].charAt(0).toUpperCase() + str[i].substring(1);
                                    }
                                    const familyheadname = str.join(' ');
                                    setData({ ...data, familyhead: familyheadname })
                                }}
                            />
                        }
                    />
                </Grid>
                <RadioGroup
                    row
                    aria-label="Baptizm"
                    name="row-radio-buttons-group"
                    style={{
                        width: 300,
                        justifyContent: "space-evenly"
                    }}
                >
                    <FormControlLabel
                        control={<Radio />}
                        value={true}
                        label={"Baptized"}
                        onChange={(event) => setData({ ...data, baptized: event.target.value })}
                    />
                    <FormControlLabel
                        value={false}
                        control={<Radio />}
                        label={"Not Baptized"}
                        onChange={(event) => setData({ ...data, baptized: event.target.value })}
                    />
                </RadioGroup>

                <RadioGroup
                    row
                    aria-label="mobile-type"
                    name="row-radio-buttons-group"
                    style={{
                        width: 300,
                        justifyContent: "space-evenly"
                    }}
                >
                    <FormControlLabel
                        label={"Android"}
                        value={true}
                        control={<Radio />}
                        onChange={(event) => setData({ ...data, android: event.target.value })} />
                    <FormControlLabel
                        label={"Normal"}
                        value={false}
                        control={<Radio />}
                        onChange={(event) => setData({ ...data, android: event.target.value })} />
                </RadioGroup>

                <Button
                    variant="contained"
                    onClick={addrecords} >
                    Add to records</Button>
            </Grid>
            <Grid>
                {JSON.stringify(data)}
            </Grid>
        </Grid>
    )
}

export default Dataentry;
