import { AlertMessage } from './Support/AlertMessage'


const Dataentry = () => {
  const [name, setName] = useState('')
  const [dob, setDob] = useState('')
  const [familyheads, setFamilyheads] = useState([])
  const [validity, setvalidity] = useState(true);
  const [notify, setNotify] = useState({ isOpen: false, message: '', variant: 'filled', severity: 'error' });

  const [data, setData] = useState({
    name: '',
    dob: new Date(),
    gender: 'male',
    weddingdate: new Date().toLocaleDateString(),
    emailid: '',
    mobile: '',
    address: '',
    familyhead: '',
    familyid: '',
    selfid: '',
    android: false,
    baptized: false,
    married: false,
  })

  const str = dob.split('-')
  const today = new Date()
  const mon = today.getMonth()
  const year = today.getFullYear()
  let age

  let k = year - 1

  if (mon >= str[1]) {
    age = year - str[0]
  } else {
    age = k - str[0]
  }

  const [validated, setValidated] = useState(false)

  const handleSubmit = (event) => {
    const form = event.currentTarget

    if (form.checkValidity() === false) {
      event.preventDefault()

      event.stopPropagation()
    }
    else {
      addrecords();
    }

    setValidated(true)
  }

  const addrecords = () => {
    if (validity) {
      let Familyid = data.familyid
      let Selfid = data.selfid
      let Name = data.name
      let Dob = data.dob
      let Age = age
      let Gender = data.gender
      let Married = data.married
      let Weddingdate = data.weddingdate
      let Emailid = data.emailid
      let Mobile = data.mobile
      let Address = data.address
      let Familyhead = data.familyhead
      let Baptized = data.baptized
      let Android = data.android

      fetch(`${url}/jehovanissi/api/member_details.php`, {
        method: 'POST',
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: Name,
          dob: Dob,
          age: Age,
          gender: Gender,
          married: Married,
          weddingdate: Weddingdate,
          emailid: Emailid,
          mobile: Mobile,
          address: Address,
          familyhead: Familyhead,
          baptized: Baptized,
          android: Android,
          familyid: Familyid,
          selfid: Selfid,
        }),
      })
        .then((res) => res.json())
        .then((result) => {
          setNotify({
            ...notify,
            isOpen:true,
            message:result
          })
          console.log(result)
        })
        .catch((error) => {
          console.error(error)
        })
    } else {
      setNotify({
        ...notify,
        isOpen:true,
        message:"Input all required"
      })
    }
  }

  const searchfamilyhead = () => {
    fetch(`${url}/jehovanissi/api/searchfamilyhead.php`, {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
      },
    })
      .then((res) => res.json())
      .then((result) => {
        let filteredresult = [...new Set(result)]
        setFamilyheads(filteredresult)
      })
      .catch((error) => {
        console.error(error)
      })
  }
  useEffect(() => {
    searchfamilyhead()
  }, [])

  return (
    <CForm direction={'column'} alignItems={'center'} justifyItems={'stretch'} container item validated={validated} onSubmit={handleSubmit} >
      <AlertMessage
        notify={notify}
        setNotify={setNotify}
      />
      <CCol md={4}>
        <CFormLabel>
          Family ID
        </CFormLabel>
        <TextField
          style={{ width: 300, marginRight: 15 }}
          name="familyid"
          value={data.familyid}
          margin="dense"
          variant="standard"
          onChange={(event) => {
            setData({ ...data, familyid: event.target.value.toUpperCase() })
          }}
          placeholder="Family ID"
          label="Family ID"
          required
          invalid={data.familyid ? false : true}
        />
        <CFormFeedback
          invalid
        >Field is required</CFormFeedback>
      </CCol>
      <CCol md={4} >
        <CFormLabel>
          Self ID
        </CFormLabel>
        <TextField
          name="selfid"
          style={{ width: 300, marginRight: 15 }}
          value={data.selfid}
          margin="dense"
          variant="standard"
          onFocus={() => setData({ ...data, selfid: data.familyid.slice(3) })}
          placeholder="Self ID"
          label="Self ID"
          required
          readOnly
          invalid={data.selfid ? false : true}
        />
        <CFormFeedback
          invalid
        >
          Field is required
        </CFormFeedback>
      </CCol>

      <CCol>
        <CFormLabel>
          Name
        </CFormLabel>
        <TextField
          name="name"
          value={data.name}
          style={{ width: 300, marginRight: 15 }}
          margin="dense"
          variant="standard"
          onChange={(event) => {
            const str = event.target.value.split(' ')
            for (let i = 0; i < str.length; i++) {
              str[i] = str[i].charAt(0).toUpperCase() + str[i].substring(1)
            }
            const patt = /[^a-zA-Z ]/g;
            const Name1 = str.join(' ').replace(patt, '');
            setData({ ...data, name: Name1 })
          }}
          placeholder="Enter Name"
          label="Enter Name"
          invalid={data.name ? false : true}
          required
        />
        <CFormFeedback
          invalid
        >
          Please fill this field
        </CFormFeedback>
      </CCol>
      <CCol md={4} >
        <CFormLabel>
          Date Of Birth
        </CFormLabel>
        <TextField
          type={'date'}
          style={{ width: 300, marginRight: 15 }}
          value={data.dob}
          onChange={(event) => setData({ ...data, dob: event.target.value })}
          label="Enter DOB"
          required
          invalid={data.dob ? false : true}
        />
        <CFormFeedback
          invalid
        >
          Field is required
        </CFormFeedback>
      </CCol>
      <CCol md={4} >
        <CFormLabel>
          Gender
        </CFormLabel>
        <RadioGroup
          row
          aria-label="gender"
          name="row-radio-buttons-group"
          style={{
            width: 300,
            justifyContent: 'space-evenly',
          }}
        >
          <FormControlLabel
            value="male"
            name="gender"
            label={'Male'}
            control={<Radio />}
            onChange={(event) => setData({ ...data, gender: event.target.value })}
          />
          <FormControlLabel
            value="female"
            name="gender"
            label={'Female'}
            control={<Radio />}
            onChange={(event) => setData({ ...data, gender: event.target.value })}
          />
        </RadioGroup>
      </CCol>
      <CCol md={4} >
        <CFormLabel>
          Martial status
        </CFormLabel>
        <RadioGroup
          row
          aria-label="martial-status"
          name="row-radio-buttons-group"
          style={{
            width: 300,
            justifyContent: 'space-evenly',
          }}
        >
          <FormControlLabel
            value={true}
            label={'Married'}
            control={<Radio />}
            onChange={(event) => {
              setData({ ...data, married: event.target.value == 'true' ? true : false })
            }}
          />
          <FormControlLabel
            value={false}
            control={<Radio />}
            label={'Unmarried'}
            onChange={(event) => {
              setData({ ...data, married: event.target.value == 'true' ? true : false })
            }}
          />
        </RadioGroup>
      </CCol>
      <CCol md={4}
        style={{
          display: data.married === true ? 'block' : 'none',
          width: 300,
          marginRight: 15,
        }}
      >
        <CFormLabel>
          Wedding Date
        </CFormLabel>
        <TextField
          type="date"
          label="Wedding Date"
          margin="dense"
          variant="standard"
          value={data.weddingdate}
          onChange={(event) => setData({ ...data, weddingdate: event.target.value })}
        />
      </CCol>

      <CCol>
        <CFormLabel>
          Email
        </CFormLabel>
        <TextField
          name="emailid"
          placeholder='Enter Emailid'
          margin="dense"
          label="Email ID"
          style={{ width: 300, marginRight: 15 }}
          variant="standard"
          onChange={(event) => setData({ ...data, emailid: event.target.value })}
          value={data.emailid}
          invalid={(!data.emailid || (data.emailid.includes('@') && data.emailid.includes('.com'))) ? false : true}
        />
        <CFormFeedback
          invalid
        >
          Please input valid email address
        </CFormFeedback>
      </CCol>
      <CCol>
        <CFormLabel>
          Mobile
        </CFormLabel>
        <TextField
          name="mobile"
          style={{ width: 300, marginRight: 15 }}
          label="Mobile"
          placeholder='Enter mobile number'
          margin="dense"
          variant="standard"
          value={data.mobile}
          onChange={(event) => {
            let patt = /[^0-9]/g
            const ph = event.target.value.replace(patt, '')
            setData({ ...data, mobile: ph.slice(0, 10) })
          }}
          required
          invalid={(data.mobile.length === 10) ? false : true}
        />
        <CFormFeedback invalid>
          Enter valid number
        </CFormFeedback>
      </CCol>



      <CCol>
        <CFormLabel>
          Address
        </CFormLabel>
        <CFormTextarea
          name="address"
          label="Address"
          placeholder='Enter address'
          style={{ width: 300, marginRight: 15 }}
          margin="dense"
          variant="standard"
          value={data.address}
          onChange={(event) => {
            const str = event.target.value.split(',')
            for (let i = 0; i < str.length; i++) {
              str[i] = str[i].charAt(0).toUpperCase() + str[i].substring(1)
            }
            const address = str.join(',')
            setData({ ...data, address: address })
          }}
          required
          invalid={data.address ? false : true}
        />
        <CFormFeedback
          invalid
        >
          Field is required
        </CFormFeedback>
      </CCol>
      <CCol md={4}>
        <CFormLabel>
          Family head
        </CFormLabel>
        <TextField
          name="familyhead"
          value={data.familyhead}
          margin="dense"
          placeholder='Enter family head'
          variant="standard"
          label="Family Head"
          onChange={(event) => {
            const str = event.target.value.split(' ')
            for (let i = 0; i < str.length; i++) {
              str[i] = str[i].charAt(0).toUpperCase() + str[i].substring(1)
            }
            const familyheadname = str.join(' ')
            setData({ ...data, familyhead: familyheadname })
          }}
          required
          invalid={data.familyhead ? false : true}
          list='listforfamilyheads'
        />
        <datalist id='listforfamilyheads'>
          {familyheads.map((f, key) => {
            return (
              <option key={key} >{f}</option>
            )
          })}
        </datalist>
        <CFormFeedback invalid>
          Field is required
        </CFormFeedback>
      </CCol>
      <CCol md={4}>
        <CFormLabel>
          Baptism
        </CFormLabel>
        <RadioGroup
          row
          aria-label="Baptizm"
          name="row-radio-buttons-group"
          style={{
            width: 300,
            justifyContent: 'space-evenly',
          }}
        >
          <FormControlLabel
            control={<Radio />}
            value={true}
            label={'Baptized'}
            onChange={(event) =>
              setData({ ...data, baptized: event.target.value == 'true' ? true : false })
            }
          />
          <FormControlLabel
            value={false}
            control={<Radio />}
            label={'Not Baptized'}
            onChange={(event) =>
              setData({ ...data, baptized: event.target.value == 'true' ? true : false })
            }
          />
        </RadioGroup>
      </CCol>
      <CCol md={4}>
        <CFormLabel>
          Mobile type
        </CFormLabel>
        <RadioGroup
          row
          aria-label="mobile-type"
          name="row-radio-buttons-group"
          style={{
            width: 300,
            justifyContent: 'space-evenly',
          }}
        >
          <FormControlLabel
            label={'Android'}
            value={true}
            control={<Radio />}
            onChange={(event) =>
              setData({ ...data, android: event.target.value == 'true' ? true : false })
            }
          />
          <FormControlLabel
            label={'Normal'}
            value={false}
            control={<Radio />}
            onChange={(event) =>
              setData({ ...data, android: event.target.value == 'true' ? true : false })
            }
          />
        </RadioGroup>
      </CCol>

      <CButton type='submit' >
        Add to records
      </CButton>
    </CForm>
  )
}

export default Dataentry
