import React, { useState } from "react";
import { url } from "../Constants";
import { Grid, TextField, Button, FormControlLabel, RadioGroup, Radio } from "@mui/material";

const Dataentry = () => {
    const [name, setName] = useState('');
    const [dob, setDob] = useState('');
    const [gender, setGender] = useState('');
    const [married, setMarried] = useState(false);
    const [weddingdate, setWeddingdate] = useState('01/01/1997');
    const [emailid, setEmailid] = useState('');
    const [mobile, setMobile] = useState();
    const [address, setAddress] = useState('');
    const [familyhead, setFamilyhead] = useState('');
    const [baptized, setBaptized] = useState(false);
    const [android, setAndroid] = useState(true);
    const [familyid, setFamilyid] = useState('');
    const [selfid, setSelfid] = useState('');
    const [display, setdisplay] = useState("none");
    const [familyheads, setFamilyheads] = useState([]);
    const [validity, setvalidity] = useState();


    const str = dob.split('-');
    const today = new Date();
    const mon = today.getMonth();
    const year = today.getFullYear();
    let age;



    let k = year - 1;

    if (mon >= str[1]) {
        age = year - str[0];


    }
    else {
        age = k - str[0];

    }

    const addrecords = () => {
        if ((familyid && familyhead && address && selfid && name && gender && (!mobile.length < 10)) !== '') {
            setvalidity(true);
        }
        else {
            setvalidity(false);
        }

        if (validity) {

            let Familyid = familyid;
            let Selfid = selfid;
            let Name = name;
            let Dob = dob;
            let Age = age;
            let Gender = gender;
            let Married = married;
            let Weddingdate = weddingdate;
            let Emailid = emailid;
            let Mobile = mobile;
            let Address = address;
            let Familyhead = familyhead;
            let Baptized = baptized;
            let Android = android;


            fetch(`${url}/jehovanissi/backend/apis/member_details.php`, {
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({

                    name: Name,
                    dob: Dob,
                    age: Age,
                    gender: Gender,
                    married: Married,
                    weddingdate: Weddingdate,
                    emailid: Emailid,
                    mobile: Mobile,
                    address: Address,
                    familyhead: Familyhead,
                    baptized: Baptized,
                    android: Android,
                    familyid: Familyid,
                    selfid: Selfid
                })
            })
                .then(res => res.json())
                .then((result) => {
                    alert(result);
                    window.location.reload();
                }).catch((error) => {
                    console.error(error);
                });
        }
        else {
            alert("Input All required");

        }



    }

    const searchfamilyhead = () => {
        fetch(`${url}/jehovanissi/backend/apis/searchfamilyhead.php`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },

        })
            .then(res => res.json())
            .then((result) => {
                let filteredresult = [...new Set(result)];
                setFamilyheads(filteredresult);
            }).catch((error) => {
                console.error(error);
            });
    }


    return (
        <Grid
            direction="column"
            alignItems={"center"}
            justifyContent={"center"}
            justifyItems={"center"}
            container
        >
            <TextField
                name="familyid"
                value={familyid}
                margin="dense"
                variant="outlined"
                onChange={(event) => setFamilyid(event.target.value.toUpperCase())}
                placeholder="Family ID"
                label="Family ID"
            />

            <TextField
                name="selfid"
                value={selfid}
                variant="outlined"
                placeholder="Self ID"
                label="Self ID"
            />
            <TextField
                name="name"
                value={name}
                variant="outlined"
                onChange={(event) => {
                    const str = event.target.value.split(' ');
                    for (let i = 0; i < str.length; i++) {

                        str[i] = str[i].charAt(0).toUpperCase() + str[i].substring(1);
                    }

                    const Nameoff = str.join(' ');
                    setName(Nameoff)
                }}
                placeholder="Enter Name"
                label="Enter Name"
            />
            <TextField
                type="date"
                name="dob"
                value={dob}
                variant="outlined"
                onChange={(event) => setDob(event.target.value)}
                placeholder="Enter DOB"
                label="Enter DOB"
            />
            <Grid>
                <RadioGroup row aria-label="gender" name="row-radio-buttons-group">
                    <FormControlLabel
                        value="male"
                        name="gender"
                        label={"Male"}
                        control={<Radio />}
                        onChange={(event) => setGender(event.target.value)} />
                    <FormControlLabel
                        value="female"
                        name="gender"
                        label={"Female"}
                        control={<Radio />}
                        onChange={(event) => setGender(event.target.value)} />
                </RadioGroup>
                <RadioGroup row aria-label="martial-status" name="row-radio-buttons-group">
                    <FormControlLabel
                        value={true}
                        label={"Married"}
                        control={<Radio />}
                        onChange={(event) => { setMarried(event.target.value === 'true' ? event.target.value = true : event.target.value = false); setdisplay(event.target.value === "true" ? "block" : "none") }} />
                    <FormControlLabel
                        value={false}
                        control={<Radio />}
                        label="Unmarried"
                        onChange={(event) => { setMarried(event.target.value === 'false' ? event.target.value = false : event.target.value = true); setdisplay(event.target.value === "false" ? "none" : "block") }} />
                </RadioGroup>
            </Grid>
            <TextField style={{ display: display }}
                type="date"
                label="Wedding Date"
                variant="outlined"
                defaultValue={"01/01/2020"}
                onChange={(event) => setWeddingdate(event.target.value)}
            />
            <TextField
                name="emailid"
                label="Email ID"
                variant="outlined"
                onChange={(event) => setEmailid(event.target.value)}
                value={emailid}
            />
            <TextField
                name="mobile"
                label="Mobile"
                variant="outlined"
                value={mobile}
                onChange={(event) => {
                    let patt = /[^0-9]/g;
                    const ph = event.target.value.replace(patt, '');
                    setMobile(ph.slice(0, 10));
                }} />

            <TextField
                name="address"
                label="Address"
                variant="outlined"
                value={address}
                onChange={(event) => setAddress(event.target.value)}
            />
            <TextField
                name="familyhead"
                value={familyhead}
                variant="outlined"
                label="Family Head"
                onChange={(event) => {
                    searchfamilyhead();
                    const str = event.target.value.split(' ');
                    for (let i = 0; i < str.length; i++) {

                        str[i] = str[i].charAt(0).toUpperCase() + str[i].substring(1);
                    }

                    const familyheadname = str.join(' ');

                    setFamilyhead(familyheadname)
                }}
                list="searchfhead"
            />
            <datalist id="searchfhead">
                {familyheads.map((k, key) => {
                    return (
                        <option key={key}>{k}</option>
                    )
                })}
            </datalist>
            <RadioGroup row aria-label="Baptizm" name="row-radio-buttons-group" >
                <FormControlLabel
                    control={<Radio />}
                    value={true}
                    label={"Baptized"}
                    onChange={(event) => setBaptized(event.target.value === 'true' ? event.target.value = true : event.target.value = false)}
                />
                <FormControlLabel
                    value={false}
                    control={<Radio />}
                    label={"Not Baptized"}
                    onChange={(event) => setBaptized(event.target.value === 'false' ? event.target.value = false : event.target.value = true)}
                />
            </RadioGroup>

            <RadioGroup row aria-label="mobile-type" name="row-radio-buttons-group">
                <FormControlLabel
                    label={"Android"}
                    value={true}
                    control={<Radio />}
                    onChange={(event) => setAndroid(event.target.value === 'true' ? event.target.value = true : event.target.value = false)} />
                <FormControlLabel
                    label={"Normal"}
                    value={false}
                    control={<Radio />}
                    onChange={(event) => setAndroid(event.target.value === 'false' ? event.target.value = false : event.target.value = true)} />
            </RadioGroup>

            <Button
                variant="contained"
                onClick={addrecords} >
                Add to records</Button>
        </Grid>
    )
}

export default Dataentry;
