import React, { forwardRef } from 'react'
import PropTypes from 'prop-types'

import { CDateRangePicker, CDateRangePickerProps } from '../daterangepicker/CDateRangePicker'

export interface CDatePickerProps
  extends Omit<
    CDateRangePickerProps,
    | 'calendars'
    | 'endDate'
    | 'range'
    | 'onEndDateChange'
    | 'onStartDateChange'
    | 'ranges'
    | 'selectEndDate'
    | 'startDate'
  > {
  /**
   * Initial selected date.
   */
  date?: Date | string | null
  /**
   * Callback fired when the date changed.
   */
  onDateChange?: (date: Date | null) => void
}

export const CDatePicker = forwardRef<HTMLDivElement | HTMLLIElement, CDatePickerProps>(
  ({ date, onDateChange, placeholder = 'Select date', ...rest }, ref) => {
    return (
      <CDateRangePicker
        calendars={1}
        startDate={date}
        onStartDateChange={onDateChange}
        placeholder={placeholder}
        range={false}
        ref={ref}
        {...rest}
      />
    )
  },
)

CDatePicker.displayName = 'CDatePicker'

CDatePicker.propTypes = {
  ...CDateRangePicker.propTypes,
  date: PropTypes.oneOfType([PropTypes.instanceOf(Date), PropTypes.string]),
  onDateChange: PropTypes.func,
}
